import React, {useState} from "react";
import fetchData from "./services/api";
import Card from "./components/Card";
import initialData from "./helpers/initialData";

function App() {
  const [city, setCity] = useState('')
  const [data, setData] = useState(initialData)

  const handleSubmit = (event) => {
    event.preventDefault() // Preserva que o evento ocorra sem recarregar a página.
    fetchData(city).then((response) => {
      setData(response)


    })
  }

  return (
    <>
      <div className="flex flex-col w-full h-screen items-center justify-center p-4">
        <form onSubmit={handleSubmit} className="fixed bottom-0 w-full flex">
          <input 
            type="text" 
            placeholder="Cidade" 
            className="p-3 rounded-lg outline-none"
            onChange={({target: {value}}) => setCity(value)}
            value={city}
          />
          <button 
            type="Submit"
            className="bg-blue-600 p-3 rounded-lg ml-3 text-white font-bold"
          >
            Pesquisar
          </button>
        </form>

        <Card data = {data}/>
      </div>
    </>
  );
}

export default App;
